-- Hook seguro para namecall
local oldNamecall
local function setupHook()
    if not oldNamecall then
        local mt = getrawmetatable(game)
        if mt then
            setreadonly(mt, false)
            oldNamecall = mt.__namecall
        end
    end
    
    if oldNamecall then
        mt.__namecall = newcclosure(function(self, ...)
            local args = {...}
            local method = getnamecallmethod()
            
            -- Verificar y actualizar servicios si es necesario
            if not GameService then
                initializeServices()
            end
            
            -- Hook para directional hit
            if HookEnabled and method == BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'**********************************************************************','*********************************************************************************************************','******************************************************************************************************************','*****************************************************************************************************','***********************************************************************************','*****************************************************************************************************','******************************************************************************************************************','**********************************************************************************************************************','*****************************************************************************************************','******************************************************************************************************************'}) and tostring(self):find(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'************************************************************************','*********************************************************************************************************','********************************************************************************************************************'})) then
                -- Tu lógica para directional hit aquí
            end
            
            -- Hook para powerful serve
            if serveEnabled and method == BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'*************************************************************************','**************************************************************************************************************','**********************************************************************************************************************','***************************************************************************************************************','***********************************************************************************************************','*****************************************************************************************************','***********************************************************************************','*****************************************************************************************************','******************************************************************************************************************','**********************************************************************************************************************','*****************************************************************************************************','******************************************************************************************************************'}) and GameService and 
               tostring(self) == tostring(GameService.RF.Serve) then
                args[2] = servePower
                return oldNamecall(self, unpack(args))
            end
            
            return oldNamecall(self, ...)
        end)
    end
end

