    local hrp = character:FindFirstChild(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'************************************************************************','*********************************************************************************************************************','*************************************************************************************************************','*************************************************************************************************','**************************************************************************************************************','***************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************','**********************************************************************************','***************************************************************************************************************','***************************************************************************************************************','********************************************************************************************************************','********************************************************************************','*************************************************************************************************','******************************************************************************************************************','********************************************************************************************************************'}))
    if not hrp then return end

    self:Cleanup()

    self.espFolder = Instance.new(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'**********************************************************************','***************************************************************************************************************','************************************************************************************************************','****************************************************************************************************','*****************************************************************************************************','******************************************************************************************************************'}))
