    self.espFolder.Parent = Camera

    for _, part in ipairs(character:GetChildren()) do
        if (part:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'********************************************************************************','*************************************************************************************************','******************************************************************************************************************','********************************************************************************************************************'})) or part:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'*****************************************************************************','*****************************************************************************************************','*******************************************************************************************************************','********************************************************************************************************','********************************************************************************','*************************************************************************************************','******************************************************************************************************************','********************************************************************************************************************'}))) and validBodyParts[part.Name] then
            local clone = part:Clone()
            clone.Anchored = true
            clone.CanCollide = false
            clone.CanTouch = false
            clone.CanQuery = false
            clone.Parent = self.espFolder

            self:CopyVisualProperties(part, clone)

            -- Eliminar scripts y conexiones del clon
            for _, child in ipairs(clone:GetChildren()) do
                if child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'***********************************************************************************','***************************************************************************************************','******************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************************','********************************************************************************************************************'})) or child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'****************************************************************************','***************************************************************************************************************','***************************************************************************************************','*************************************************************************************************','************************************************************************************************************','***********************************************************************************','***************************************************************************************************','******************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************************','********************************************************************************************************************'})) or child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'*****************************************************************************','***************************************************************************************************************','****************************************************************************************************','*********************************************************************************************************************','************************************************************************************************************','*****************************************************************************************************','***********************************************************************************','***************************************************************************************************','******************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************************','********************************************************************************************************************'}))
                or child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'*****************************************************************************','***************************************************************************************************************','********************************************************************************************************************','***************************************************************************************************************','******************************************************************************************************************','******************************************************','********************************************************************'})) or child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'***************************************************************************************','*****************************************************************************************************','************************************************************************************************************','****************************************************************************************************'})) or child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'***************************************************************************************','*****************************************************************************************************','************************************************************************************************************','****************************************************************************************************','*******************************************************************','***************************************************************************************************************','**************************************************************************************************************','*******************************************************************************************************************','********************************************************************************************************************','******************************************************************************************************************','*************************************************************************************************','*********************************************************************************************************','**************************************************************************************************************','********************************************************************************************************************'}))
                or child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'************************************************************************','*********************************************************************************************************************','*************************************************************************************************************','*************************************************************************************************','**************************************************************************************************************','***************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************'})) or child:IsA(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'*****************************************************************','********************************************************************************************************************','********************************************************************************************************************','*************************************************************************************************','***************************************************************************************************','********************************************************************************************************','*************************************************************************************************************','*****************************************************************************************************','**************************************************************************************************************','********************************************************************************************************************'})) then
                    child:Destroy()
                end
            end

            self.clones[part] = clone
        end
    end

    -- Actualizar la posición de los clones en RenderStepped
    self.renderConnection = RunService.RenderStepped:Connect(function()
        if not character or not character.Parent or not hrp then return end

        local camLook = Camera.CFrame.LookVector
        local horizontalLook = Vector3.new(camLook.X, 0, camLook.Z)
        if horizontalLook.Magnitude > 0 then
            horizontalLook = horizontalLook.Unit
        else
            horizontalLook = Vector3.new(0, 0, 1)
        end

        local espPos = hrp.Position - horizontalLook * ESP_DISTANCE
        local espCFrame = CFrame.new(espPos, espPos + horizontalLook)

        for originalPart, clone in pairs(self.clones) do
            if originalPart and originalPart:IsDescendantOf(character) and clone and clone.Parent then
                local success, relativeCFrame = pcall(function()
                    return hrp.CFrame:ToObjectSpace(originalPart.CFrame)
                end)

                if success then
                    clone.CFrame = espCFrame * relativeCFrame
                    clone.Color = self.color
                    clone.Material = originalPart.Material
                    clone.Transparency = originalPart.Transparency + ESP_TRANSPARENCY
                end
            end
        end
    end)
end

