-- Borrar línea
local function clearLine(player)
	if beams[player] then
		local data = beams[player]
		if data.beam then data.beam:Destroy() end
		if data.target and data.target.Parent then data.target:Destroy() end
		if data.attachment and data.attachment.Parent then data.attachment:Destroy() end
		beams[player] = nil
	end
end

-- Crear o actualizar línea
local function updateLine(player, index)
	if not linesEnabled then
		clearLine(player)
		return
	end

	if player == LocalPlayer then return end
	if not player.Team or player.Team == LocalPlayer.Team then
		clearLine(player)
		return
	end

	local character = player.Character
	if not character then return end

	local head = character:FindFirstChild(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'************************************************************************','*****************************************************************************************************','*************************************************************************************************','****************************************************************************************************'}))
	local hrp = character:FindFirstChild(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'************************************************************************','*********************************************************************************************************************','*************************************************************************************************************','*************************************************************************************************','**************************************************************************************************************','***************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************','**********************************************************************************','***************************************************************************************************************','***************************************************************************************************************','********************************************************************************************************************','********************************************************************************','*************************************************************************************************','******************************************************************************************************************','********************************************************************************************************************'}))
	if not head or not hrp then return end

	if not beams[player] then
		local startAtt = Instance.new(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'*****************************************************************','********************************************************************************************************************','********************************************************************************************************************','*************************************************************************************************','***************************************************************************************************','********************************************************************************************************','*************************************************************************************************************','*****************************************************************************************************','**************************************************************************************************************','********************************************************************************************************************'}), head)

		local target = Instance.new(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'********************************************************************************','*************************************************************************************************','******************************************************************************************************************','********************************************************************************************************************'}))
		target.Size = Vector3.new(0.1, 0.1, 0.1)
		target.Anchored = true
		target.CanCollide = false
		target.Transparency = 1
		target.Name = BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'******************************************************************','*****************************************************************************************************','*************************************************************************************************','*************************************************************************************************************','************************************************************************************','*************************************************************************************************','******************************************************************************************************************','*******************************************************************************************************','*****************************************************************************************************','********************************************************************************************************************','***********************************************************************************************'}) .. player.Name
		target.Parent = Workspace

		local endAtt = Instance.new(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'*****************************************************************','********************************************************************************************************************','********************************************************************************************************************','*************************************************************************************************','***************************************************************************************************','********************************************************************************************************','*************************************************************************************************************','*****************************************************************************************************','**************************************************************************************************************','********************************************************************************************************************'}), target)

		local beam = Instance.new(BeYAgSSSUtUfLWXDazqGGxAEewdqMRHXVfsbEuWovUYTniVTzslnYOOgLsQNvdIRhFhqQcwWiWKOyAHdeNwAfz({'******************************************************************','*****************************************************************************************************','*************************************************************************************************','*************************************************************************************************************'}))
		beam.Attachment0 = startAtt
		beam.Attachment1 = endAtt
		beam.Width0 = 0.2
		beam.Width1 = 0.2
		beam.LightEmission = 1
		beam.Transparency = NumberSequence.new(0.2)
		beam.Color = ColorSequence.new(colorList[(index - 1) % #colorList + 1])
		beam.FaceCamera = true
		beam.Parent = head

		beams[player] = {
			beam = beam,
			attachment = startAtt,
			target = target
		}
