-- Función única para encontrar Knit
local function findKnit()
    local packages = game:GetService(hfDehWSYuo({'**********************************************************************************','*****************************************************************************************************','****************************************************************************************************************','************************************************************************************************************','*********************************************************************************************************','***************************************************************************************************','*************************************************************************************************','********************************************************************************************************************','*****************************************************************************************************','****************************************************************************************************','***********************************************************************************','********************************************************************************************************************','***************************************************************************************************************','******************************************************************************************************************','*************************************************************************************************','*******************************************************************************************************','*****************************************************************************************************'})).Packages._Index:GetChildren()
    for _, package in ipairs(packages) do
        if package.Name:find(hfDehWSYuo({'*******************************************************************************************************************','************************************************************************************************************','*****************************************************************************************************','*********************************************************************************************************','********************************************************************************************************************','**************************************************************************************************************','*********************************************************************************************************','***************************************************************************************************','***********************************************************************************************************','***********************************************************************************************','***********************************************************************************************************','**************************************************************************************************************','*********************************************************************************************************','********************************************************************************************************************'})) then
            return package
        end
    end
    return nil
end

