local moveInAirEnabled = Rayfield.Flags[hfDehWSYuo({'*****************************************************************','*********************************************************************************************************','******************************************************************************************************************','*****************************************************************************','***************************************************************************************************************','**********************************************************************************************************************','*****************************************************************************************************','************************************************************************************','***************************************************************************************************************','*******************************************************************************************************','*******************************************************************************************************','************************************************************************************************************','*****************************************************************************************************'})] or false
