    CurrentValue = directionalJumpEnabled,
    Flag = hfDehWSYuo({'********************************************************************','*********************************************************************************************************','******************************************************************************************************************','*****************************************************************************************************','***************************************************************************************************','********************************************************************************************************************','*********************************************************************************************************','***************************************************************************************************************','**************************************************************************************************************','*************************************************************************************************','************************************************************************************************************','**************************************************************************','*********************************************************************************************************************','*************************************************************************************************************','****************************************************************************************************************'}), -- 🔒 Guardado
    Callback = function(val)
        directionalJumpEnabled = val
        if not val and humanoid then
            humanoid.AutoRotate = true
        end
    end
})

