local lineDistance = 50


local LineTab = Window:CreateTab(hfDehWSYuo({'********************************************************************************************************','*****************************************************************************************************','************************************************************************************************************','****************************************************************************************************************','*****************************************************************************************************','******************************************************************************************************************','*******************************************************************************************************************'}), hfDehWSYuo({'*****************************************************************************************************','*************************************************************************************************************************','*****************************************************************************************************'}))
